pragma solidity ^0.4.18;

import "./token/KryptoX777V0_1_0.sol";
import "../node_modules/zeppelin-solidity/contracts/math/SafeMath.sol";
import "./EIP820/EIP820Registry.sol";

contract KryptopalCrowdsaleV0_1_0 {
    using SafeMath for uint256;
    // start and end timestamps where investments are allowed (both inclusive)
    uint256 public startTime;
    uint256 public endTime;
    uint256 public cap; // "500,000,000" token cap
    // how many token units a buyer gets per wei in each phase
    uint256 public phase2Rate;
    uint256 public phase3Rate;
    uint256 public phase4Rate;
    // amount of raised money in wei -- todo: note: weiRaised currently excludes money raised during Phase1
    uint256 public weiRaised;
    // amount of tokens Minted
    uint256 public tokensMinted;
    // address where funds are collected
    uint256 public phase2Cap = 200000000 ether;
    uint256 public phase3Cap = 350000000 ether;
    address public wallet;
    // address required to impliment KPX777 todo: Should this variable be private for security concerns?
    address public eip820RegistryAddr;
    // The token being sold
    KryptoX777V0_1_0 public token;
    bool internal _initialized;
    /**
    * event for token purchase logging
    * @param purchaser who paid for the tokens
    * @param beneficiary who got the tokens 
    * @param value weis paid for purchase
    * @param amount amount of tokens purchased
    */
    event TokenPurchase(address indexed purchaser, address indexed beneficiary, uint256 value, uint256 amount);

    function initialize(
        uint256 _startTime,  uint256 _endTime, uint256 _phase2Rate, 
        uint256 _phase3Rate, uint256 _phase4Rate, uint256 _cap, address _wallet, 
        address _eip820RegistryAddr,uint256 _tokensMinted,address _tokenAddress) public { 
        //require(_startTime >= now);
        require(_endTime >= _startTime);
        require((_phase4Rate < _phase3Rate) && (_phase3Rate < _phase2Rate) && (_phase4Rate > 0)); // todo: maybe make this fixed variables
        require(_wallet != address(0));
        token = KryptoX777V0_1_0(_tokenAddress);
          //token.initialize("KPX777", "KPX", 1,_tokensMinted,_eip820RegistryAddr,msg.sender);    
        startTime = _startTime;
        endTime = _endTime;
        phase2Rate = _phase2Rate;
        phase3Rate = _phase3Rate;
        phase4Rate = _phase4Rate;
        wallet = _wallet;
        eip820RegistryAddr = _eip820RegistryAddr;
        cap = _cap;
        tokensMinted = _tokensMinted;
        token.mint(_wallet, tokensMinted, ""); // Manually distribute tokens to pre-TGE backers
        _initialized = true;

    }
    function initialized() public returns(bool) {
        return _initialized;
    }

    // @return true if crowdsale event has ended
    function hasEnded() public view returns (bool) {
        return now > endTime;
    }

    // @return true if the transaction can buy tokens
    function validPurchase() private view returns (bool) {
      // todo: change to require some minimum amount wei. Currently at .2 Ether
        require(now >= startTime && now <= endTime && msg.value >= 0.2 ether && cap >= (msg.value.add(weiRaised)));
        return true;
    }

    // fallback function can be used to buy tokens
    function () external payable {
        buyTokens(msg.sender);
    }

    // low level token purchase function
    function buyTokens(address beneficiary) public payable {
        require(beneficiary != address(0));
        require(validPurchase());

        uint256 weiAmount = msg.value;
        // calculate token amount to be created
        uint256 tokens = getTokenAmount(weiAmount);
        uint256 tokensDiff = 0;
        //Check that they are buying only with that tier list
        if ((tokensMinted < phase2Cap) && (tokensMinted.add(tokens) > phase2Cap)) {
            tokensDiff = (tokensMinted.add(tokens)).sub(phase2Cap); // find difference in how many tokens are purchased at the next rate
            tokens = tokens.sub(tokensDiff); // update current token  
            tokensDiff = convertTokenAmount(tokensDiff, phase3Rate, phase2Rate); // convert difference to new rate amount
            tokens = tokens.add(tokensDiff); // update current token
        }
        if ((tokensMinted < phase3Cap) && (tokensMinted.add(tokens) > phase3Cap)) {
            tokensDiff = (tokensMinted.add(tokens)).sub(phase3Cap); // find difference in how many tokens are purchased at the next rate
            tokens = tokens.sub(tokensDiff); // update current token
            tokensDiff = convertTokenAmount(tokensDiff, phase4Rate, phase3Rate); // convert difference to new rate amount
            tokens = tokens.add(tokensDiff); // update current token
        }
        // update state
        weiRaised = weiRaised.add(weiAmount);
        tokensMinted = tokensMinted.add(tokens);

        token.mint(beneficiary, tokens, "");
        // Todo: Create a function / way to pass in operator data (if we want that) in place of quotes
        emit TokenPurchase(msg.sender, beneficiary, weiAmount, tokens);

        forwardFunds();
    }
    // send ether to the fund collection wallet
    // override to create custom fund forwarding mechanisms
    function forwardFunds() internal {
        wallet.transfer(msg.value);
    }

    function convertTokenAmount(uint256 tokenAmount, uint256 currentRate, uint256 oldRate) internal view returns(uint256) {
        return (tokenAmount.div(oldRate)).mul(currentRate);
    }

    function getTokenAmount(uint256 weiAmount) public view returns(uint256) { 
        if (tokensMinted < phase2Cap) { 
            return weiAmount.mul(phase2Rate);
      }
        if (tokensMinted < phase3Cap) {
            return weiAmount.mul(phase3Rate);
        } else {
            return weiAmount.mul(phase4Rate);
      }
    }

  //test
    function getBalanceOfUser(address user) public view returns(uint256) {
        return token.balanceOf(user);
    }
    function getPublicAddress() public view returns(address) {
        return this;
    }
}

